    <script>
        $('.next').click(function() {
            let no = $(this).prev().text();
            $(this).prev().text(parseInt(no) + 1);
        });
        $('.prev').click(function() {
            let no = $(this).next().text();
            if (no == 0) {
                $(this).next().text(0);
            } else {
                $(this).next().text(parseInt(no) - 1);
            }

        });
    </script>
